function CenteredText(text, w, textsize, textfont, x, y, color, flip)% CenteredText(text, w, textsize, textfont, x, y, color, flip)% Displays a text centered on x, y % w = window% If the value of the variable flip is 1, then the text will be shown on the next screen% refreshment% Otherwise, the flip will have to be done after calling this function% (useful if you want to draw several lines of text, in different% positions, and display them simultaneously)Screen('TextSize', w , textsize);Screen('TextFont', w , textfont);[Bounds]= Screen('TextBounds', w, text);Screen('DrawText',w, text, x-(Bounds(3)/2), y-(Bounds(4)/2), color);if flip == 1    Screen('Flip', w);end